con:config.script:
import groovy.json.JsonSlurper

def response = context.testCase.getTestStepByName("REST Request for Generate Access Token").getPropertyValue("response")

log.info response

def holder = new JsonSlurper().parseText(response)

def access = holder.data.access_token
context.testCase.testSuite.project.setPropertyValue("access",access)

import jxl.*
def filepath = "/REST Project_Product.xls"
File Excel = new File(filepath)
Workbook wb = Workbook.getWorkbook(Excel)
Sheet sh = wb.getSheet("Sheet1")
def rowsize=sh.getRows().toInteger()
def columnsize=sh.getColumns().toInteger()

for(int i=0;i<rowsize;i++){
	def Model
	def Quantity
	def Price
	for(int j=0;j<columnsize;j++){
		     if(j==0)
			 Model=sh.getCell(j,i).getContents()
			if(j==1)
			 Quantity=sh.getCell(j,i).getContents()
			if(j==2)
			 Price=sh.getCell(j,i).getContents()

	}


           log.info Model
           log.info Quantity
           log.info Price

def model = context.testCase.testSuite.project.testSuites["TestSuite 1"].testCases["TC_022_AddNewProduct_Modify"].testSteps["Product Properties"].setPropertyValue("model",Model)
def quantity = context.testCase.testSuite.project.testSuites["TestSuite 1"].testCases["TC_022_AddNewProduct_Modify"].testSteps["Product Properties"].setPropertyValue("quantity",Quantity)
def price = context.testCase.testSuite.project.testSuites["TestSuite 1"].testCases["TC_022_AddNewProduct_Modify"].testSteps["Product Properties"].setPropertyValue("price",Price)
}

